import React, { Component } from "react";

class SearchBar extends Component {

    constructor(props) {
        super(props)

        this.state = {
             searchItems : {
                 firstName: '',
                 lastName:'',
                 email:''
             }
        }
    }


    handleChange = (e) =>{
     const name= e.target.name
     const value= e.target.value;
     console.log(e.target.value)
     let  items ={...this.state.searchItems}
     items[name]= value;

     this.setState({
         searchItems:items
     })
     console.log(this.state.searchItems)
    }
  render() {
    return (
      <div className="display-inline m-3">

          <input type="text" className="m-2" placeholder="firstname" 
          name="firstName"
          onChange={this.handleChange}
          />

           <input type="text"  className="m-2" placeholder="Lastname" 
          name="lastName"
          onChange={this.handleChange}/>

           <input type="text"  placeholder="Email" 
           name="email"
          onChange={this.handleChange}/>

        <button type="submit"
        onClick={this.handleClick}
        className="btn btn-default">
          Submit
        </button>
      </div>
    );
  }
}

export default SearchBar;
