import React, { Component } from "react";

class SearchBar extends Component {

    constructor(props) {
        super(props)

        this.state = {
             searchItems : {
                 firstName: '',
                 lastName:'',
                 email:''
             }
        }
    }


    handleChange = (e) =>{
     const name= e.target.name
     const value= e.target.value;
     console.log(e.target.name)
     let  items ={...this.state.searchItems}
     items[name]= value;

     this.setState({
         items
     })
    }
  render() {
    return (
      <div className="display-inline m-3">

          <input type="text" className="m-2" placeholder="firstname" 
          name="firstName"
          onChange={this.handleChange}
          />

           <input type="text"  className="m-2" placeholder="Lastname" 
          name="lastName"
          value={this.state.searchItems.lastName || ''}/>

           <input type="text"  placeholder="Email" 
          name="email"
          value={this.state.searchItems.email || ''}/>

        <button type="submit" className="btn btn-default">
          Submit
        </button>
      </div>
    );
  }
}

export default SearchBar;
